import React from 'react'
import { Spinner } from 'react-bootstrap'

function Loader() {
  return (
    <Spinner
    animation="border"
    role ="status"
    style = {{
        width: '100px',
        height: '100px',
        margin: 'auto',
        display: 'block'
    }}  
    >
    <span className = "sr-only">Please Wait...</span>
    </Spinner>
  )
}

export default Loader
