return (
    <>
      {['md'].map((expand) => (
        <Navbar key={expand} bg="light" expand={expand} className="mb-3">dfg
          <Container>
            <Navbar.Brand >Sara Fashion</Navbar.Brand>dfg
            <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} />
            dfg
            <Navbar.Offcanvas
              id={`offcanvasNavbar-expand-${expand}`}
              aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}fg
              placement="start"
              
            >
            
              <Offcanvas.Header closeButton>
                <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}> 
                  Sara Fashion
                </Offcanvas.Title>
              </Offcanvas.Header>
              <Offcanvas.Body>
                <Nav className="justify-content-center flex-grow-1 pe-3">
                  <LinkContainer to="/">
                    <Nav.Link className = "me-5">Home</Nav.Link>
                  </LinkContainer>
                  <LinkContainer to="/catalog">
                    <Nav.Link className = "me-5">Catalog</Nav.Link>
                  </LinkContainer>
                  <LinkContainer to="/about">
                    <Nav.Link className = "me-5">About Us</Nav.Link>
                  </LinkContainer>
                  <LinkContainer to="/contact">
                    <Nav.Link className = "me-5">Contact Us</Nav.Link>
                  </LinkContainer>
                </Nav>
                {/* <Form className="d-flex">
                  <Form.Control
                    type="search"
                    placeholder="Search"
                    className="me-2"
                    aria-label="Search"
                  />
                  <Button variant="outline-success">Search</Button>
                </Form> */}

                <div className="d-flex"  text='dark'>
                     
                  <LinkContainer to="/login">
                      <Navbar.Brand><i className = "fas fa-user"></i>Login</Navbar.Brand>
                  </LinkContainer>
                  <LinkContainer variant = "dark" to="/cart">
                    <Navbar.Brand>
                    
                      <i className = "fas fa-shopping-cart"><span className="cartCount">{cartItems.reduce((acc, item) =>  acc+item.qty ,0)}</span></i></Navbar.Brand>
                  </LinkContainer>
                </div>
          
               
              </Offcanvas.Body>
            </Navbar.Offcanvas>
          </Container>
        </Navbar>
      ))}
    </>
  );
}

export default Header;
